const Discord = require('discord.js');
var rpcEmotes = ["ðŸŒ‘", "ðŸ“ƒ", "âœ‚ï¸"];
var interval;
var emojiTimerRe = 0;
var mainMessage;

var embedTemplate = new Discord.RichEmbed({
    color: 0x0000FF,
    author: {
        name: client.user.username,
        icon_url: client.user.avatarURL
    },
    title: "Rock Paper Scissors",
    description: `Rock (${rpcEmotes[0]}), Paper (${rpcEmotes[1]}), or Scissors (${rpcEmotes[2]})?\n\nReact below within 1 minute.`,
    timestamp: new Date(),
});

msg.channel.send(embedTemplate).then(async (message) => {
    mainMessage = message;
    interval = setInterval(function() {
        emojiTimerInt(message, rpcEmotes);
    }, 500)

})

function emojiTimerInt(message, subarray) {
    message.react(subarray[emojiTimerRe]);
    emojiTimerRe += 1;

    if (emojiTimerRe == subarray.length) {
        clearInterval(interval);

        message.awaitReactions((reaction, user) => rpcEmotes.includes(reaction.emoji.name) && user.id == msg.author.id, {
                time: 60000,
                errors:['time'],
                max:1
            })
            .then((collected) => {
                var emojiReacted = collected.first()["emoji"];
                var emojiBot = rpcEmotes[Math.floor(Math.random() * rpcEmotes.length)];

                var rpcList = [emojiBot,emojiReacted["name"]];

                if (rpcList.includes(rpcEmotes[0]) && rpcList.includes(rpcEmotes[1])) { // Rock + Scissor
                    if (emojiBot == rpcEmotes[0]) {
                        win(emojiBot,emojiReacted["name"]);
                    } else {
                        lose(emojiBot,emojiReacted["name"]);
                    }
                } else if (rpcList.includes(rpcEmotes[1]) && rpcList.includes(rpcEmotes[2])) { // Paper + Scissor
                    if (emojiBot == rpcEmotes[1]) {
                        win(emojiBot,emojiReacted["name"]);
                    } else {
                        lose(emojiBot,emojiReacted["name"]);
                    }
                } else if (rpcList.includes(rpcEmotes[2]) && rpcList.includes(rpcEmotes[0])) { // Scissor + Rock
                    if (emojiBot == rpcEmotes[2]) {
                        win(emojiBot,emojiReacted["name"]);
                    } else {
                        lose(emojiBot,emojiReacted["name"]);
                    }
                } else { // Tie
                    tie(emojiBot,emojiReacted["name"]);
                }
            })
            .catch((error) => {
                var errorEmbed = new Discord.RichEmbed({
                    color: 0xFF0000,
                    author: {
                        name: client.user.username,
                        icon_url: client.user.avatarURL
                    },
                    title: "Rock Paper Scissors",
                    description: "You did not provide a reaction in time!",
                    timestamp: new Date(),
                });

                msg.channel.send(errorEmbed);
            });
    }
}

function win(botOption, playerOption) {
    embedTemplate["description"] = `Rock (${rpcEmotes[0]}), Paper (${rpcEmotes[1]}), or Scissors (${rpcEmotes[2]})?\n\nI chose ${botOption}\nYou chose ${playerOption}\n\nYou win!`;
    embedTemplate["color"] = 0x00FF00;
    mainMessage.edit(embedTemplate);
}

function lose(botOption, playerOption) {
    embedTemplate["description"] = `Rock (${rpcEmotes[0]}), Paper (${rpcEmotes[1]}), or Scissors (${rpcEmotes[2]})?\n\nI chose ${botOption}\nYou chose ${playerOption}\n\nYou lose!`;
    embedTemplate["color"] = 0xFF0000;
    mainMessage.edit(embedTemplate)
}

function tie(botOption, playerOption) {
    embedTemplate["description"] = `Rock (${rpcEmotes[0]}), Paper (${rpcEmotes[1]}), or Scissors (${rpcEmotes[2]})?\n\nI chose ${botOption}\nYou chose ${playerOption}\n\nWe tied!`;
    embedTemplate["color"] = 0xFFFF00;
    mainMessage.edit(embedTemplate)
}
