function errorMessage(error, errorCause = "none") {

    if (debug) {
        console.log(errorCause);
    }

    if (mainMessage != null) {
        mainMessage.delete();
    }

    let errorTemplate = new Discord.RichEmbed({
        color: 0xFF0000,
        author: {
            name: client.user.username,
            icon_url: client.user.avatarURL
        },
        title: "Giveaway Setup",
        description: error,
        timestamp: new Date(),
    })

    msg.channel.send(errorTemplate);
