// event
if (globalVars("inviteManagerRanAlready") == null) {
    var inviteDatabase = {};
    var checkUpdates = true;

    Actions.storeValue(true, 3, "inviteManagerRanAlready", cache);

    const fs = require('fs');
    const request = require('request');

    if (checkUpdates) {
        request('https://raw.githubusercontent.com/ItzPike/DBM-Rawdata-Releases/master/Invite%20Manager/versionCheck.json', function(error, response, body) {
            if (response.statusCode == 200) {
                var allVersions = JSON.parse(body);

                var commandVersions = {};
                var eventVersions = {};
                var commandKeys = [];
                var eventKeys = [];

                allVersions["commands"].forEach(commandVer => {
                    commandVersions[commandVer[0]] = commandVer[1];
                    commandKeys.push(commandVer[0]);
                });

                allVersions["events"].forEach(eventVer => {
                    eventVersions[eventVer[0]] = eventVer[1];
                    eventKeys.push(eventVer[0])
                })

                fs.readFile("./data/commands.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemCommands = await data.filter(command => command != null && commandKeys.includes(command["_id"]));

                    for (i = 0; i < systemCommands.length; i++) {
                        var systemActionFind = await systemCommands[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemActionFind.length != 0) {
                            var versionCheckSplit = systemActionFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit[1] != null && versionCheckSplit[1] != commandVersions[systemCommands[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Invite Manager] The command ${systemCommands[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })

                fs.readFile("./data/events.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemEvent = await data.filter(event => event != null && eventKeys.includes(event["_id"]));

                    for (i = 0; i < systemEvent.length; i++) {
                        var systemEventFind = await systemEvent[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemEventFind.length != 0) {
                            var versionCheckSplit = systemEventFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit != null && versionCheckSplit[versionCheckSplit.length - 1] != eventVersions[systemEvent[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Invite Manager] The event ${systemEvent[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })
            }
        });
    }

    Actions.inviteModifyData = function(userID, guildID, valueChange, invalidReal) {
        invalidReal = invalidReal;
        if (invalidReal == 1 || invalidReal == 0) {
            if (invalidreal == 1) {
                invalidReal = "real";
            } else {
                invalidReal = "invalid";
            }

            if (!isNaN(valueChange)) {
                var invitePData = globalVars('invitePData');
                var inviterData = invitePData["inviterData"];

                if (!checkProperty(inviterData, guildID)) {
                    inviterData[guildID] = {};
                }

                if (!checkProperty(inviterData[guildID], userID)) {
                    inviterData[guildID][userID] = {
                        "invites": [],
                        "data": {
                            "real": 0,
                            "invalid": 0
                        }
                    };
                }


                inviterData[guildID][userID]["data"][invalidReal] += valueChange;
                if (inviterData[guildID][userID]["data"][invalidReal] < 0) {
                    inviterData[guildID][userID]["data"][invalidReal] = 0;
                }

                writeToData("inviteManagerData", invitePData, "invitePData");

                return "success";

            } else {
                return "failed - Invalid value";
            }
        } else {
            return "failed - Invalid value change selection";
        }
    }

    Actions.inviteGet = function(userID, guildID, invalidReal) {
        invalidReal = invalidReal;
        if (invalidReal == 1 || invalidReal == 0) {
            if (invalidreal == 1) {
                invalidReal = "real";
            } else {
                invalidReal = "invalid";
            }

            var invitePData = globalVars('invitePData');
            var inviterData = invitePData["inviterData"];

            if (checkProperty(inviterData, guildID) && checkProperty(inviterData[guildID], userID)) {
                return inviterData[guildID][userID]["data"][invalidReal];
            } else {
                return "failed - No data found";
            }
        } else {
            return "failed - Invalid value change selection";
        }
    }

    Actions.fetchDatabase = function() {
        return inviteDatabase;
    }

    client.guilds.cache.forEach(g => {
        g.fetchInvites().then(guildInvites => {
            try {
                inviteDatabase[g.id] = guildInvites;
            } catch (error) {
                console.log(error);
            }
        });
    });


    checkReadFile("inviteManagerData", {
        "inviterData": {},
        "invitedData": {}
    }, "invitePData");
    checkReadFile("inviteManagerSettings", {}, "inviteSettings");
    checkReadFile("inviteManagerInviteData", {}, "inviteData");



    client.on('guildMemberAdd', (member) => {
        var invitePData = globalVars('invitePData');
        var inviteSettings = globalVars('inviteSettings');
        var inviteData = globalVars('inviteData');

        var memberGuild = member.guild;
        var memberGuildID = memberGuild.id;

        var guildInv = inviteDatabase[memberGuildID];

        memberGuild.fetchInvites().then(guildInvites => {
            inviteDatabase[memberGuildID] = guildInvites;
            const invited = guildInvites.find(i => guildInv.get(i.code).uses < i.uses);
            const inviter = client.users.cache.get(invited.inviter.id);

            var inviterID = inviter.id.toString();
            var invitedID = member.id.toString();

            if (!checkProperty(inviteData, invited.code)) {
                inviteData[invited.code] = [];
            }

            var memInviteDataIndex = inviteData[invited.code].findIndex(memberN => memberN == member.id);
            if (memInviteDataIndex == -1) {
                inviteData[invited.code].push(member.id);
            } else {
                inviteData[invited.code].splice(memInviteDataIndex, 1);
                inviteData[invited.code].push(member.id);
            }

            writeToData("inviteManagerInviteData", inviteData, "inviteData");


            var memberTime = member.user.createdTimestamp / 1000;
            var currentTime = Math.floor(Date.now() / 1000);

            var memberAge = Math.floor((currentTime - memberTime) / 86400);


            if (!checkProperty(invitePData['inviterData'], memberGuildID)) {
                invitePData['inviterData'][memberGuildID] = {};
            }
            if (!checkProperty(invitePData['inviterData'][memberGuildID], inviterID)) {
                invitePData['inviterData'][memberGuildID][inviterID] = {
                    "invites": [],
                    "data": {
                        "real": 0,
                        "invalid": 0
                    }
                };
            }



            if (!checkProperty(invitePData['invitedData'], memberGuildID)) {
                invitePData['invitedData'][memberGuildID] = {};
            }
            if (!checkProperty(invitePData['invitedData'][memberGuildID], invitedID)) {
                invitePData['invitedData'][memberGuildID][invitedID] = []
            }


            var inviterData = invitePData['inviterData'][memberGuildID][inviterID];
            var invitedData = invitePData['invitedData'][memberGuildID][invitedID];

            for (i = 0; i < inviterData["invites"].length; i++) {
                if (inviterData["invites"][i]["id"] == invitedID) {
                    if (inviterData["invites"][i]["valid"]) {
                        inviterData["data"]["real"] -= 1;
                    } else {
                        inviterData["data"]["invalid"] -= 1;
                    }
                    inviterData["invites"].splice(i, 1);
                }
            }

            inviterData["invites"].push({
                "id": member.id,
                "time": currentTime,
                "codeUsed": invited.code,
                "valid": true,
                "invalidCause": ""
            });

            if (checkProperty(inviteSettings, memberGuildID) && inviteSettings[memberGuildID]["invalidize"]["age_check"]["enabled"]) {
                if (memberAge >= inviteSettings[memberGuildID]["invalidize"]["age_check"]["minimum"]) {
                    inviterData["data"]["real"] += 1;
                } else {
                    var latestIndex = inviterData["invites"].length - 1;
                    inviterData["invites"][latestIndex].valid = false;
                    inviterData["invites"][latestIndex].invalidCause = "Player did not meet the minimum account age on join.";
                    inviterData["data"]["invalid"] += 1;
                }
            } else {
                inviterData["data"]["real"] += 1;
            }

            for (i = 0; i < invitedData.length; i++) {
                if (invitedData[i] == inviterID) {
                    invitedData.splice(i, 1);
                }
            }

            invitedData.push(inviterID);

            writeToData("inviteManagerData", invitePData, "invitePData");

            if (checkProperty(inviteSettings, memberGuildID)) {
                if (inviteSettings[memberGuildID]["logging"]["enabled"]) {
                    var fetchedChannel = memberGuild.channels.cache.get(inviteSettings[memberGuildID]["logging"]["channel"]);

                    if (fetchedChannel != null) {


                        var guildData = invitePData["inviterData"][memberGuildID];
                        var inviteArr = [];

                        var objectKeys = Object.keys(guildData);

                        sortedItems = "";
                        for (i = 0; i < objectKeys.length; i++) {
                            var keyIndex = objectKeys[i];
                            guildData[keyIndex]["id"] = keyIndex;
                            inviteArr.push(guildData[keyIndex]);
                        }

                        var sortedArr = inviteArr.sort(function(a, b) {
                            return a["data"]["real"] - b["data"]["invalid"]
                        });

                        var leaderboardStanding = sortedArr.findIndex(userData => userData["id"] == inviterID) + 1;


                        var format = inviteSettings[memberGuildID]["logging"]["format"]
                        fetchedChannel.send({
                            embed: {
                                title: `Welcome to ${memberGuild.name}!`,
                                description: format.replace('{user1}', `${member}`).replace('{user2}', `${inviter}`).replace('{code}', `${invited.code}`).replace('{uses}', `${invited.uses}`),
                                thumbnail: {
                                    url: member.user.avatarURL(),
                                },
                                fields: [{
                                        name: 'ðŸ‘¤ Inviter',
                                        value: inviter,
                                        inline: true,
                                    },
                                    {
                                        name: 'âœ… Real Invites',
                                        value: inviterData["data"]["real"],
                                        inline: true,
                                    },
                                    {
                                        name: 'ðŸ† Rank',
                                        value: `#${leaderboardStanding}`,
                                        inline: true,
                                    }
                                ],
                                "color":0x00ff00,
                                timestamp: new Date()
                            }
                        });
                    } else {
                        inviteSettings[memberGuildID]["logging"]["enabled"] = false;
                        inviteSettings[memberGuildID]["logging"]["channel"] = "0";

                        writeToData("inviteManagerSettings", inviteSettings, "inviteSettings");
                    }
                }
            }

        });
    });

    client.on('guildMemberRemove', async (member) => {
        var invitePData = globalVars('invitePData');
        var memberGuild = member.guild;
        var memberGuildID = memberGuild.id;
        var invitedData = invitePData['invitedData'];
        if (checkProperty(invitedData, memberGuildID) && checkProperty(invitedData[memberGuildID], member.id) && invitedData[memberGuildID][member.id].length != 0) {
            var memberInviters = invitedData[memberGuildID][member.id];
            var inviterData = invitePData['inviterData'][memberGuildID][memberInviters[memberInviters.length - 1]];

            var findInviteIndex = inviterData["invites"].findIndex(inviteMem => inviteMem.id == member.id);
            if (inviterData["invites"][findInviteIndex]["valid"]) {
                inviterData["invites"][findInviteIndex]["valid"] = false;
                inviterData["invites"][findInviteIndex]["invalidCause"] = "User had left the server";

                inviterData["data"]["invalid"] += 1;
                inviterData["data"]["real"] -= 1;
            }

            writeToData("inviteManagerData", invitePData, "invitePData");
        }
    });

    client.on('guildCreate', (guild) => {
        guild.fetchInvites(invites => {
            inviteDatabase[guild.id] = invites;
        })
    });

    client.on('inviteCreate', (invite) => {
        if (inviteDatabase[invite.guild.id] == null) {
            inviteDatabase[invite.guild.id] = new Map();
        }
        inviteDatabase[invite.guild.id].set(invite.code, invite);
    })

    function checkReadFile(name, format, dataname) {
        if (fs.existsSync(`./data/${name}.json`)) {
            fs.readFile(`./data/${name}.json`, function read(err, data) {
                if (err) {
                    throw err;
                }
                data = JSON.parse(data)

                Actions.storeValue(data, 3, dataname, cache);
            });
        } else {
            writeToData(name, format, dataname);
        }
    }

    function writeToData(name, data, dataname) {
        fs.writeFile(`./data/${name}.json`, JSON.stringify(data), function(err) {
            if (err) {
                return console.log(err);
            }

            Actions.storeValue(data, 3, dataname, cache);
        });
    }

    function checkProperty(item, check) {
        if (item[check] == null) {
            return false;
        } else {
            return true;
        }
    }
}

