const Discord = require("discord.js")
let reactionArray = [];
let rectarray = globalVars('reactionRoleInf');
const _this = this;
let emojiArray = [];
let roleArray = [];
let errorCatch = 5;
let localEmoji;
let localEmojiG;
let emojiTimerRe = 0;
var interval;
let regex = /[^\u0000-\u00ff]/;
const fs = require('fs');

function specCheck(str) {
    if (!str.length) return false;
    if (str.charCodeAt(0) > 255) return true;
    return regex.test(str);
}

let format;

let embedTemplate = new Discord.RichEmbed( {
    color: 0x00FF00,
    author: {
      name: client.user.username,
      icon_url: client.user.avatarURL
    },
    title: "Role Reaction Prompt",
    description: "This is a test embed to showcase what they look like and what they can do.",
    timestamp: new Date(),
})

// Settings
let grabAllEmoji = false; // Set true if you want the bot to use global emojis from all the servers its in. (May cause slowdowns in larger servers)
let roleLimit = 10; // Default: 10 || Sets the maximum number of roles attached to emojis.
//

if (grabAllEmoji) {
    localEmoji = (client || this.getDBM().Bot.bot).emojis.array();
    localEmojiG = "Please make sure the emoji you selected is in one of the servers that the bot is in!";
} else {
    localEmoji = msg.guild.emojis.array();
    localEmojiG = "Please make sure the emoji you selected is in this server!";
}
embedTemplate["description"] = `Welcome to the autorole promt.\n\nPlease now respond with a format for your reaction roles!\nFor ex. {emoji} => {role}\n\nUse {emoji} where the emoji will be placed and {role} where the role attached to that emoji will be placed.\n(One of {emoji} and one of {role} is required in the format)`
msg.channel.send(embedTemplate)
    .then((message) => {
        msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                max: 1,
                time: 60000,
                errors: ['time'],
            })
            .then(async function(collected) {
                msg.author.lastMessage.delete();
                if (collected.first().content.includes("{emoji}") && collected.first().content.includes("{role}")) {
                    let msgFormat = collected.first().content;
                    embedTemplate["description"] = `Your format will appear like this: \n${msgFormat.replace("{emoji}","ðŸ˜ƒ").replace("{role}","Example Role")}\n\nIf you are sure with this format, respond with "confirm"\nIf not, please respond with "cancel"`;
                    message.edit(embedTemplate);
                    msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                        max: 1,
                        time: 60000,
                        errors: ['time'],
                    })
                    .then(async function(collected) {
                        msg.author.lastMessage.delete();
                        if (collected.first().content.toLowerCase() == "confirm") {
                            format = msgFormat;
                            reAsk(message, " ");
                        } else {
                            message.delete();
                            errorMessage('Prompt cancelled, please re-execute the command to go through the prompt again!');
                        }
                    })
                } else {
                    message.delete();
                    errorMessage('Prompt cancelled, you didnt provide a valid format with both required variables!');
                }

            }).catch((error) => {
                console.log(error);
                message.delete();
                errorMessage('Sorry, you didnt provide a valid response in time!\nPlease re-execute the command to go through the prompt again!');
            });
    })

function reAsk(message, extra) {
    if (extra == null) {
        extra = "";
    }
    if (reactionArray.length != roleLimit) {
        var finalText = "";
        reactionArray.forEach(subarray => {
            finalText = finalText + format.replace("{emoji}", subarray[0]).replace("{role}",subarray[2]) + "\n"
        })
        embedTemplate["description"] = `Please now enter the emoji-role reaction connections (Max ${roleLimit})\n ${finalText}\n\nIf you would like to attach another reaction to a role, please insert a role along with an emoji.\nEx. ExampleRole ðŸ˜ƒ\nIf not, please reply with "done".\nIf you would like to cancel the prompt, please respond with "cancel"\n${extra}`
        message.edit(embedTemplate);
        msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                max: 1,
                time: 60000,
                errors: ['time'],
            })
            .then(async function(collected) {
                msg.author.lastMessage.delete();
                if (collected.first().content.toLowerCase() == "done") {
                    if (reactionArray.length > 0) {
                        done(message);
                    } else {
                        if (errorCatch > 0) {
                            errorCatch -= 1;
                            reAsk(message, `Please atleast attach 1 emoji to 1 role!\nTries left - ${errorCatch}`)
                        } else {
                            message.delete();
                            errorMessage("Prompt has been cancelled, you used the same emoji twice for differnt roles!\nPlease re-execute the command to go through the prompt again!");
                        }
                    }
                } else if (collected.first().content.toLowerCase() == "cancel") {
                    message.delete();
                    errorMessage("Prompt has been cancelled.\nPlease re-execute the command to go through the prompt again!");
                } else {
                    if (collected.first().content.split(/ +/g).length > 1) {
                        const tstValidRole = await msg.guild.roles.find(role => role.name === collected.first().content.split(/ +/g)[0]);
                        if (collected.first().mentions.roles.first() || tstValidRole != null) {
                            var unicodeStored = collected.first().content.split(/ +/g)[1];
                            if (collected.first().mentions.roles.first() != null) {
                                roleMentioned = collected.first().mentions.roles.first();
                            } else {
                                roleMentioned = tstValidRole;
                            }
                            if (unicodeStored != null) {
                                if (!emojiArray.includes(unicodeStored)) {
                                    if (!roleArray.includes(roleMentioned.id)) {
                                        if (specCheck(unicodeStored) || unicodeStored.includes("<:") || unicodeStored.includes("<a:")) {
                                            var emoji;
                                            if (unicodeStored.includes("<:") || unicodeStored.includes("<a:")) {
                                                unicodeStored = unicodeStored.replace(/\D/g, '');
                                                emoji = localEmoji.filter(emojiT => emojiT.id == unicodeStored);
                                                if (emoji.length > 0) {
                                                    unicodeStored = emoji[0];
                                                    delete unicodeStored['guild']
                                                    reactionArray.push([unicodeStored, roleMentioned.id, roleMentioned.name]);
                                                    emojiArray.push(unicodeStored);
                                                    roleArray.push(roleMentioned.id);
                                                    reAsk(message);
                                                } else {
                                                    if (errorCatch > 0) {
                                                        errorCatch -= 1;
                                                        reAsk(message, `Please provide a valid custom emoji!\n${localEmojiG}\nTries left - ${errorCatch}`);
                                                    } else {
                                                        message.delete();
                                                        errorMessage(`Prompt has been cancelled, you provided an invalid custom emoji!\n${localEmojiG}\nPlease re-execute the command to go through the prompt again!`);
                                                    }
                                                }
                                            } else {
                                                reactionArray.push([unicodeStored, roleMentioned.id, roleMentioned.name]);
                                                emojiArray.push(unicodeStored);
                                                roleArray.push(roleMentioned.id);
                                                reAsk(message);
                                            }

                                        } else {
                                            if (errorCatch > 0) {
                                                errorCatch -= 1;
                                                reAsk(message, `Please don't use the same emoji twice!\nTries left - ${errorCatch}`)
                                            } else {
                                                message.delete();
                                                errorMessage("Prompt has been cancelled, you used the same emoji twice for differnt roles!\nPlease re-execute the command to go through the prompt again!");
                                            }
                                        }
                                    } else {
                                        if (errorCatch > 0) {
                                            errorCatch -= 1;
                                            reAsk(message, `Please don't use the same role twice!\nTries left - ${errorCatch}`)
                                        } else {
                                            message.delete();
                                            errorMessage("Prompt has been cancelled, you used the same role twice in the prompt!\nPlease re-execute the command to go through the prompt again!");
                                        }
                                    }
                                } else {
                                    if (errorCatch > 0) {
                                        errorCatch -= 1;
                                        reAsk(message, `Please provide a valid emoji!\nTries left - ${errorCatch}`)
                                    } else {
                                        message.delete();
                                        errorMessage("Prompt has been cancelled, you didn't provide a valid emoji!\nPlease re-execute the command to go through the prompt again!");
                                    }
                                }
                            }
                        } else {
                            if (errorCatch > 0) {
                                errorCatch -= 1;
                                reAsk(message, `Please mention a valid role!\nTries left - ${errorCatch}`)
                            } else {
                                message.delete();
                                errorMessage("Prompt has been cancelled, you didn't mention a valid role!\nPlease re-execute the command to go through the prompt again!");
                            }
                        }
                    } else {
                        if (errorCatch > 0) {
                            errorCatch -= 1;
                            reAsk(message, `Please provide a valid emoji/role!\nTries left - ${errorCatch}`)
                        } else {
                            message.delete();
                            errorMessage("Prompt has been cancelled, you didn't provide a emoji/mentioned role!\nPlease re-execute the command to go through the prompt again!");
                        }
                    }
                }
            }).catch(() => {
                message.delete();
                errorMessage('Sorry, you didnt provide a valid response in time!\nPlease re-execute the command to go through the prompt again!');
            });
    } else {
        done(message);
    }

}

