    })
    embedTemplate["description"] = `Final conformation menu\n\nBelow is the list of reactions to their roles\n${finalText}\n\nIf this is correct, please respond with a mention to a text channel where the role menu will be sent.\nIf this isn't correct, respond with "cancel" to cancel the prompt.`;
    message.edit(embedTemplate);
    msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
            max: 1,
            time: 60000,
