        });
}

function emojiTimerInt(message, subarray) {
    message.react(subarray[emojiTimerRe][0])
    emojiTimerRe += 1;

    if (emojiTimerRe == subarray.length) {
        clearInterval(interval);
    }
}

function errorMessage(error) {
    let errorTemplate = new Discord.RichEmbed( {
        color: 0xFF0000,
        author: {
          name: client.user.username,
          icon_url: client.user.avatarURL
        },
        title: "Role Reaction Prompt",
        description: error,
        timestamp: new Date(),
    })

    msg.channel.send(errorTemplate);
